#EXTRACT SAMPLES ONE VS. ONE
#Main function, call some others
#5% samples
indexX<-251
percent<-0.10
for(k in 1:20)
{
    iter<-k
    actualSamples<-extractSample(indexX, percent)
    #base
    for(i in 1:255)
    {
      actualSampleRow<-extractSampleRow(actualSamples,i)
      actualData<-data[which(data$V1001==indexX),]
      actualData<-actualData[actualSampleRow,]
    
      if(i>=indexX){ indexY<-i+1 } else { indexY<-i }
      auxSample<-extractSample(indexY, percent)
      auxSampleRow<-extractSampleRow(auxSample,sample(1:nrow(auxSample),1))
      auxData<-data[which(data$V1001==indexY),]
      auxData<-auxData[auxSampleRow,]
    
      finalData<-rbind(actualData,auxData)
      finalData<-getfinaldata(finalData)
      finalData$dd.V1001[finalData$dd.V1001==indexY] <- "-1"
      finalData$dd.V1001[finalData$dd.V1001==indexX] <- "1"
      write.table(finalData,paste(indexX,"/",percent*100,"percent/","data",iter,"/","samplebase",indexX,"_",indexY,".csv", sep=""),row.names=FALSE, quote=FALSE, col.names=FALSE, sep=",")
    }
    #transfer
    for(i in 256:510)
    {
      actualSampleRow<-extractSampleRow(actualSamples,i)
      actualData<-data[which(data$V1001==indexX),]
      actualData<-actualData[actualSampleRow,]
      
      if((i-255)>=indexX){ indexY<-(i-255)+1 } else { indexY<-(i-255) }
      auxSample<-extractSample(indexY, percent)
      auxSampleRow<-extractSampleRow(auxSample,sample(1:nrow(auxSample),1))
      auxData<-data[which(data$V1001==indexY),]
      auxData<-auxData[auxSampleRow,]
      
      finalData<-rbind(actualData,auxData)
      finalData<-getfinaldata(finalData)
      finalData$dd.V1001[finalData$dd.V1001==indexY] <- "-1"
      finalData$dd.V1001[finalData$dd.V1001==indexX] <- "1"
      write.table(finalData,paste(indexX,"/",percent*100,"percent/","data",iter,"/","sampletransfer",indexX,"_",indexY,".csv", sep=""),row.names=FALSE, quote=FALSE, col.names=FALSE, sep=",")
    }
}
    
