#SAMPLING FUNCTIONS
extractSample<-function(class, percentage)
{
  sampling(data[which(data$V1001==class),],class, percentage)
}

sampling<-function(dataX, indexX, percentage)
{
  samp<-t(replicate(255*2,(sample(1:nrow(dataX),nrow(dataX)*percentage))))
}

extractSampleRow<-function(samp, row)
{
  samp[row,]
}

